-- =============================================================
-- POPOLAMENTO FAMILY
-- =============================================================

INSERT INTO family (id, name, description) VALUES (1, 'Lamiaceae', 'Erbe aromatiche ricche di oli essenziali, molto usate in fitoterapia.'),(2, 'Asteraceae', 'Una delle famiglie botaniche più estese, con molte specie medicinali.'),(3, 'Rutaceae', 'Famiglia degli agrumi, ricchi di composti aromatici e vitamina C.'),(4, 'Apiaceae', 'Piante con radici o semi aromatici, usate come officinali e spezie.'),(5, 'Asphodelaceae', 'Include Aloe e altre piante succulente a uso medicinale.'),(6, 'Plantaginaceae', 'Specie erbacee usate per proprietà lenitive e antinfiammatorie.'),(7, 'Urticaceae', 'Piante ricche di minerali con utilizzo depurativo e antinfiammatorio.'),(8, 'Hypericaceae', 'Famiglia nota per l’iperico e il suo impiego erboristico.'),(9, 'Valerianaceae', 'Piante con proprietà sedative, utili per ansia e insonnia.'),(10, 'Ginkgoaceae', 'Antichissima famiglia botanica con utilizzo vascolare.'),(11, 'Amaryllidaceae', 'Include aglio e cipolla, ricchi di composti solforati utili.'),(12, 'Musaceae', 'Famiglia tropicale che comprende il banano.'),(13, 'Papaveraceae', 'Piante con alcaloidi, alcune con proprietà analgesiche.'),(14, 'Passifloraceae', 'Rampicanti con effetti sedativi naturali.'),(15, 'Cannabaceae', 'Specie aromatiche con proprietà rilassanti.'),(16, 'Rosaceae', 'Famiglia ricca di frutti e piante medicinali antiossidanti.');
-- =============================================================
--POPOLAMENTO BENEFIT
-- =============================================================

INSERT INTO benefit (id, name, description) VALUES (1, 'Antinfiammatorio', 'Riduce infiammazioni nei tessuti.'),(2, 'Antiossidante', 'Neutralizza radicali liberi.'),(3, 'Sedativo', 'Favorisce rilassamento e sonno.'),(4, 'Digestivo', 'Aiuta digestione e riduce gonfiore.'),(5, 'Antibatterico', 'Inibisce crescita batterica.'),(6, 'Antivirale', 'Ostacola replicazione virale.'),(7, 'Rafforza il sistema immunitario', 'Stimola le difese naturali.'),(8, 'Antispasmodico', 'Riduce crampi e spasmi.'),(9, 'Calmante', 'Riduce stress e tensione.'),(10, 'Depurativo', 'Aiuta eliminazione tossine.'),(11, 'Expectorante', 'Favorisce espulsione muco.'),(12, 'Cicatrizzante', 'Accelera rigenerazione tessuti.');
-- =============================================================
-- POPOLAMENTO CONTROINDICATION
-- =============================================================

INSERT INTO controindication (id, name, description) VALUES (1, 'Gravidanza', 'Alcune piante possono stimolare attività uterina.'),(2, 'Ipertensione', 'Possibile aumento della pressione.'),(3, 'Allergie note', 'Rischio di reazioni allergiche.'),(4, 'Interazioni farmacologiche', 'Possibili interferenze con farmaci.'),(5, 'Problemi gastrointestinali', 'Può irritare stomaco o intestino.'),(6, 'Disturbi cardiovascolari', 'Sconsigliata in problemi cardiaci.'),(7, 'Patologie epatiche', 'Sovraccarico del fegato.'),(8, 'Patologie renali', 'Possibile carico sui reni.');
